import { Component } from "@angular/core";

import { User } from 'src/app/shared/interfaces/user';
import { Task } from 'src/app/shared/interfaces/task';

@Component({
    selector: 'view-tasks',
    templateUrl: './view-tasks.component.html',
    styleUrls: ['./view-tasks.component.css']
})

export class ViewTasksComponent {

  user1: User = {
    id: 1,
    username: 'johndoe',
    name: 'John doe',
    role: 'Admin'
  };

  user2: User = {
    id: 2,
    username: 'janesmith',
    name: 'Jane Smith',
    role: 'Regular'
  };

  user3: User = {
    id: 3,
    username: 'bobjohnson',
    name: 'Bob Johnson',
    role: 'Admin'
  };

  user4: User = {
    id: 4,
    username: 'williamadams',
    name: 'William Adams',
    role: 'Regular'
  };

  isLoading: boolean = true;

  tasks: Task[] = [
      { id: 1, description: 'First task', user: this.user1 },
      { id: 2, description: 'Second task', user: this.user2 },
      { id: 3, description: 'Third task', user: this.user3 },
      { id: 4, description: 'Fourth task', user: this.user4 }
    ];

    updateTask(task: Task) {
      alert("Task with id '" + task.id + "' was updated successfully.");
    }

    deleteTask(task: Task) {
      alert("Task with id '" + task.id + "' was deleted successfully.");
    }

}
