import { Component } from "@angular/core";

@Component({
    selector: 'view-users',   //Component name for html tags
    templateUrl: './view-users.component.html',   //html file path
    styleUrls: ['./view-users.component.css']   //css file path
})

export class ViewUsersComponent {

    users: User[] = [
        { id: 1, username: 'user1', name: 'John Doe', role: 'Admin' },
        { id: 2, username: 'user2', name: 'Jane Smith', role: 'Regular' },
        { id: 3, username: 'user3', name: 'Bob Johnson', role: 'Admin' },
        { id: 4, username: 'user4', name: 'William Hello', role: 'Regular' }
      ];

      updateUser(user: User) {
        alert("User with id: " + user.id + " was updated successfully.");
      }

      deleteUser(user: User) {
        console.log('Delete user:', user);
        // Add your delete logic here
      }

} 

interface User {
    id: int;
    username: string;
    name: string;
    role: string;
  }
