import { Component } from "@angular/core";
import { SharedService } from 'src/app/services/shared-service';
import { Router } from '@angular/router';

@Component({
    selector: 'nav-bar',   
    templateUrl: './nav-bar.component.html',   
    styleUrls: ['./nav-bar.component.css']  
})

export class NavBarComponent {

    showViewUsers: boolean = true; 
    showAddUser: boolean = true; 
    showViewTasks: boolean = true; 
    showAddTask: boolean = true;


    constructor(private sharedService: SharedService, private router: Router) { }


    viewUsersClicked() {
        this.router.navigateByUrl('/home/view-users');

        //this.sharedService.viewUsersClicked();
    }

    addUserClicked() {
        this.sharedService.addUserClicked();
    }

    viewTasksClicked() {
        this.sharedService.viewTasksClicked();
    }

    addTaskClicked() {
        this.router.navigateByUrl('/home/add-task');
    }

    logoutClicked() {
        this.router.navigateByUrl('/login');
    }

}
