import { Component } from "@angular/core";
import { Router } from '@angular/router';

@Component({
    selector: 'add-user',   //Component name for html tags
    templateUrl: './add-user.component.html',   //html file path
    styleUrls: ['./add-user.component.css']   //css file path
})

export class AddUserComponent {

    taskDescription: string = '';

    constructor(private router: Router) { }

    onSubmit() {
        if (this.taskDescription.trim().length == 0) {
            alert("Task's description can not be empty!");
        } else {
            const confirmation = confirm('Are you sure you want to add the ask?');
            if (confirmation) {
                //TODO: API request is missing
                setTimeout(() => {
                    this.taskDescription = '';
                    alert('Task added successfully!');
                  }, 1500);
            } 
        }
    }

}
