import { Component } from "@angular/core";
import { Router } from '@angular/router';

@Component({
    selector: 'add-user',   //Component name for html tags
    templateUrl: './add-user.component.html',   //html file path
    styleUrls: ['./add-user.component.css']   //css file path
})

export class AddUserComponent {

    userName: string = '';

    constructor(private router: Router) { }

    onSubmit() {
        if (this.userName.trim().length == 0) {
            alert("User's name can not be empty!");
        } else {
            const confirmation = confirm('Are you sure you want to add the user?');
            if (confirmation) {
                //TODO: API request is missing
                setTimeout(() => {
                    this.userName = '';
                    alert('User added successfully!');
                  }, 1500);
            } 
        }
    }

}
