import { Component } from "@angular/core";

@Component({
    selector: 'view-users',   //Component name for html tags
    templateUrl: './view-users.component.html',   //html file path
    styleUrls: ['./view-users.component.css']   //css file path
})

export class ViewUsersComponent {

    users: User[] = [
        { username: 'user1', name: 'John Doe', role: 'Admin' },
        { username: 'user2', name: 'Jane Smith', role: 'Regular' },
        { username: 'user3', name: 'Bob Johnson', role: 'Admin' }
      ];

} 

interface User {
    username: string;
    name: string;
    role: string;
  }
