import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { AppComponent } from './app.component';
import { MainComponent } from 'src/app/components/main/main.component';
import { NavBarComponent } from'./components/nav-bar/nav-bar.component';
import { AddTaskComponent } from'./components/add-task/add-task.component';
import { ViewTasksComponent } from'./components/view-tasks/view-tasks.component';
import { AddUserComponent } from'./components/add-user/add-user.component';
import { ViewUsersComponent } from'./components/view-users/view-users.component';
import { LoginComponent } from './components/login/login.component';
import { RouterModule, Routes} from '@angular/router';
import { FormsModule } from '@angular/forms';
import { NoopAnimationsModule } from '@angular/platform-browser/animations';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { HomeGuard } from 'src/app/guards/home.guard'; // Adjust the path as per your project structure

const routes: Routes = [
  { path: '', redirectTo: '/home/view-tasks', pathMatch: 'full' },
  {
    path: 'home',
    component: MainComponent,
    canActivate: [HomeGuard], // Apply the guard here
    children: [
      { path: 'add-task', component: AddTaskComponent },
      { path: 'view-users', component: ViewUsersComponent },
      { path: 'add-user', component: AddUserComponent },
      { path: 'view-tasks', component: ViewTasksComponent },
    ]
  },
  { path: 'login', component: LoginComponent },
  { path: '**', redirectTo: '/home/view-tasks' }
];

@NgModule({
  declarations: [
    AppComponent,
    MainComponent,
    AddTaskComponent,
    NavBarComponent,
    ViewTasksComponent,
    ViewUsersComponent,
    AddUserComponent,
    LoginComponent
  ],
  imports: [
    BrowserModule,
    RouterModule.forRoot(routes),
    FormsModule,
    NoopAnimationsModule,
    MatProgressSpinnerModule,
    BrowserAnimationsModule
  ],
  providers: [HomeGuard],
  bootstrap: [AppComponent]
})
export class AppModule { }
